Scripts.OnTextChanged:
if self:GetText() == "" then
	self:SetText("1");
	DMACharacterFrameSpeed:SetValue(1);
else
	DMACharacterFrameSpeed:SetValue(self:GetText());
end

Scripts.OnEnterPressed:
    		if self:GetText() == "" then
    			self:SetText("1");
	    		DMACharacterFrameSpeed:SetValue(1);
	    		DMA:SetCharacterSpeed(1);
    		else
	    		DMACharacterFrameSpeed:SetValue(self:GetText());
	    		DMA:SetCharacterSpeed(self:GetText());
    		end
			self:ClearFocus();

Scripts.OnTextChanged:
if self:GetText() == "" then
	self:SetText("1");
	DMACharacterFrameScale:SetValue(1);
else
	DMACharacterFrameScale:SetValue(self:GetText());
end

Scripts.OnEnterPressed:
    		if self:GetText() == "" then
    			self:SetText("1");
	    		DMACharacterFrameScale:SetValue(1);
	    		DMA:SetCharacterScale(1);
    		else
	    		DMACharacterFrameScale:SetValue(self:GetText());
	    		DMA:SetCharacterScale(self:GetText());
    		end
			self:ClearFocus();

local text = self:GetText();
if (text == "" or string.len(text) >= 3) then
	DMA:GameObjectSearchOnEnterPressed();
end
self:ClearFocus();

Scripts.OnVerticalScroll:
if (DMA_MiniMenuContentGameobjectObjectSearchSceneGOB13:IsShown()) then
	FauxScrollFrame_OnVerticalScroll(self, offset, 32, DMA:GenerateObjectsScrollMenu());
	FauxScrollFrame_OnVerticalScroll(self, offset, 32, DMA:GenerateObjectsScrollMenu());
else
	FauxScrollFrame_OnVerticalScroll(self, offset, 32, DMA:GenerateObjectsScrollMenu());
end

Scripts.OnEditFocusLost:
local text = self:GetText();
if ( self.startNumber ~= text and text ~= "" and tonumber(self:GetText()) ~= nil ) then
	DMAUserVars["LastGOB-SCALE"] = text;
	self:SetText(tonumber(text));
	DMA:SetGOBScale();
else
	if (self.startNumber ~= nil and self.startNumber ~= "" and tonumber(self.startNumber) ~= nil) then
		DMAUserVars["LastGOB-SCALE"] = self.startNumber;
		self:SetText(self.startNumber);
		DMA:SetGOBScale();
	else
		self:SetText("0.9");
		DMAUserVars["LastGOB-SCALE"] = 0.9;
	end
end
self:SetTextColor(1, 1, 1, 1);

Scripts.OnTextChanged:
if tonumber(self:GetText()) ~= nil or self:GetText() == "" then
	self:SetTextColor(1, 1, 1, 1);
else
	self:SetTextColor(1,0,0,1);
end

