Scripts.OnClick:
        		if (DMA_MiniMenuContentGameobjectObjectSearchScene:IsShown()) then
	        		DMA:GameObjectSearchClearResults();
	        		DMA:GenerateObjectsScrollMenu();
        		else
        			DMAUserVars["LastGOB-GUID"] = 0;
					DMAUserVars["LastGOB-X"] = 0;
					DMAUserVars["LastGOB-Y"] = 0;
					DMAUserVars["LastGOB-Z"] = 0;
					DMAUserVars["LastGOB-SCALE"] = 1;
					DMAUserVars["LastGOB-OX"] = 0;
					DMAUserVars["LastGOB-OY"] = 0;
					DMAUserVars["LastGOB-OZ"] = 0;
					DMA:UpdateGOBEditBox();
					DMA:UpdateGOBParameters();
        		end

if (DMA_MiniMenuContentGameobjectObjectSearchScene:IsShown()) then
	--DMA_MiniMenuContentGameobjectChangeMode:SetText("Объект");
	self:SetText("Поиск");
	DMA_MiniMenuContentGameobjectObjectInfoScene:Show();
	DMA_MiniMenuContentGameobjectObjectSearchScene:Hide();
	DMA_MiniMenuContentGameobjectGobTar:Show();
else
	self:SetText("Объект");
	DMA_MiniMenuContentGameobjectObjectInfoScene:Hide();
	DMA_MiniMenuContentGameobjectObjectSearchScene:Show();
	DMA_MiniMenuContentGameobjectGobTar:Hide();
end

